public class mapLVL1{
        public static final String S="";

public static void mapLVL(){

        for (int i=0; i<LVLmap16.map16.length*LVLmap16.map16.length; i++)
        {
            if (Integer.parseInt(S.charAt(i)+"")==0 || Integer.parseInt(S.charAt(i)+"")==8)
                set(i%26,i/26,Integer.parseInt(S.charAt(i)+""));
            else
            set(i%26,i/26, Integer.parseInt(S.charAt(i)+"1111"));
        }

    }
    public static void set (int i, int j, int k) {
        LVLmap16.map16[i][j] = k;
    }
}
